package var;

public class floatingpoint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float y = 3.14f;
		
		System.out.println("y="+y);
		System.out.printf("floating-point:%.8f\n",y);
		System.out.printf("floating-point:%020.8f\n",y);
		System.out.printf("floating-point:%20.8f\n",y);
		System.out.printf("floating-point:%.8g\n",y);
		System.out.printf("floating-point:%.8e\n",y);
	}

}
