package three1;

public class Control {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int gender =2;
		//만약 gender의 값이 2라면 
		
		if (gender>2) {
			System.out.println("여자가 아닙니다.");
		}
		if (gender<2) {
			System.out.println("여자가 아닙니다.");
		}
		else if (gender==2){
			System.out.println("여자 입니다.");			
		}

		
	}

}
